package week12_02;

public class ifstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int point = 80;
		if(point>=80) {
			System.out.println("wow");
		}
		
		if(point>=60) {
			System.out.println("good");
		}
		
		if(point<60) {
			System.out.println("eww");
		}
	}

}
